SELECT "Payment"."amount" as "amount", count(*) as count, "amount" * count(*) as  "productRevenue" FROM "Payment" where "Payment"."createdAt" >= {{moment(DatePicker1.selectedDate).format("YYYY-MM-DD")}}::date - interval '330 minutes' and "Payment"."createdAt" <= {{moment(DatePicker2.selectedDate).format("YYYY-MM-DD")}}::date + interval '1110 minutes' and "status" = 'responseReceivedRefund' and "paymentForId" = 3125 group by "Payment"."amount"
union
SELECT null, count(*), sum("amount") FROM "Payment" where  "paymentForId" = 3125 and "createdAt" >= {{moment(DatePicker1.selectedDate).format("YYYY-MM-DD")}}::date - interval '330 minutes' and "createdAt" <= {{moment(DatePicker2.selectedDate).format("YYYY-MM-DD")}}::date + interval '1110 minutes' and "status" = 'responseReceivedRefund' ORDER BY "productRevenue" desc;
